import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Commitment } from '../interfaces';

@Component({
  selector: 'app-manage-commitments',
  templateUrl: './manage-commitments.component.html'
})
export class ManageCommitmentsComponent {
  public commitments: Commitment[] = [];

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<Commitment[]>(baseUrl + 'commitments').subscribe(result => {
      this.commitments = result;
    }, error => console.error(error));
  }
}
<h1 id="tableLabel">Manage commitments</h1>

<p>You can create new commitments here.</p>

<mat-card class="example-card">
  <mat-card-header>
    <mat-card-title>Create new commitment</mat-card-title>
  </mat-card-header>
  <mat-card-content>
    <mat-form-field appearance="fill">
      <mat-label>Input</mat-label>
      <input matInput>
    </mat-form-field>
    <mat-form-field appearance="fill">
      <mat-label>Select</mat-label>
      <mat-select>
        <mat-option value="one">First option</mat-option>
        <mat-option value="two">Second option</mat-option>
      </mat-select>
    </mat-form-field>
    <mat-form-field appearance="fill">
      <mat-label>Textarea</mat-label>
      <textarea matInput></textarea>
    </mat-form-field>>
  </mat-card-content>
  <mat-card-actions>
    <button mat-button>Create</button>
  </mat-card-actions>
</mat-card>
