import { Component, Inject, ViewChild } from '@angular/core';
import { MatAccordion } from '@angular/material/expansion';
import { HttpClient } from '@angular/common/http';
import { Commitment } from '../interfaces';


@Component({
  selector: 'app-commitment-list',
  templateUrl: './commitment-list.html'
})
export class CommitmentListComponent {
  @ViewChild(MatAccordion) accordion: MatAccordion;
  public commitments: Commitment[] = [];

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<Commitment[]>(baseUrl + 'commitments').subscribe(result => {
      this.commitments = result;
    }, error => console.error(error));
  }
}
<h2>My commitments</h2>

<p>Below you can see the list of your commitments.</p>
<p>Please make sure you answer all due and overdue commitments!</p>

<p *ngIf="!commitments?.length"><em>Loading...</em></p>

<div class="collapse-buttons">
  <button mat-button (click)="accordion.openAll()">Expand All</button>
  <button mat-button (click)="accordion.closeAll()">Collapse All</button>
</div>

<table class='table' aria-labelledby="tableLabel" *ngIf="commitments.length">
  <tbody>
    <tr *ngFor="let commitment of commitments">
      <mat-accordion>
        <mat-expansion-panel>
          <mat-expansion-panel-header>
            <mat-panel-title>
              {{commitment.title}}
              Test title!
            </mat-panel-title>
            <mat-panel-description>
              Test description!
              {{commitment.description}}
              </mat-panel-description>
            </mat-expansion-panel-header>
          <p>
            Test p!
            {{commitment.answer}}
            {{commitment.deadline}}
            {{commitment.status}}
          </p>
          </mat-expansion-panel>
        </mat-accordion>
      </tr>
    </tbody>
  </table>
