import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Commitment, CommitmentTemplate } from '../interfaces';
import { SolidCommitment } from '../classes';

@Component({
  selector: 'app-manage-commitments',
  templateUrl: './manage-commitments.component.html',
  styleUrls: ['./manage-commitments.css']
})
export class ManageCommitmentsComponent {
  public templates: CommitmentTemplate[] = [];
  public selectedTemplate: CommitmentTemplate;

  private baseUrl: string;
  private http: HttpClient;

  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this.baseUrl = baseUrl;
    this.http = http;

    this.getTemplates();
  }

  getTemplates() {
    this.http.get<CommitmentTemplate[]>(this.baseUrl + 'templates').subscribe(result => {
      this.templates = result;
      this.selectedTemplate = this.templates[0];
    }, error => console.error(error));
  }

  createCommitment(template: CommitmentTemplate) {
    let commitment = new SolidCommitment();
    commitment.title = template.title;
    commitment.description = template.description;
    commitment.deadline = template.deadline;

    console.log(commitment);
    this.sendCommitment(commitment);
  }

  sendCommitment(commitment: Commitment) {
    this.http.post<Commitment>(this.baseUrl + 'commitments', commitment);
  }
}
<h1 id="tableLabel">Manage commitments</h1>

<p>You can create new commitments here.</p>

<p *ngIf="!selectedTemplate"><em>Loading...</em></p>

<mat-card class="template-card" *ngIf="selectedTemplate">
  <mat-card-header>
    <mat-card-title>Create new commitment</mat-card-title>
  </mat-card-header>
  <mat-card-content selectedTemplate>
    <mat-form-field class="col" appearance="fill">
      <mat-label>Select template</mat-label>
      <mat-select [(ngModel)]="selectedTemplate">
        <mat-option *ngFor="let t of templates" [value]="t">
          {{t.title}}
        </mat-option>
      </mat-select>
    </mat-form-field>
    <mat-form-field class="col" appearance="fill">
      <mat-label>Title</mat-label>
      <input matInput disabled [(ngModel)]="selectedTemplate.title">
    </mat-form-field>
    <mat-form-field class="col" appearance="fill">
      <mat-label>Description</mat-label>
      <input matInput disabled [(ngModel)]="selectedTemplate.description">
    </mat-form-field>
    <mat-form-field class="col" appearance="fill">
      <mat-label>Deadline</mat-label>
      <input matInput [(ngModel)]="selectedTemplate.deadline">
    </mat-form-field>
  </mat-card-content>
  <mat-card-actions align="end">
    <button mat-button (click)="createCommitment(selectedTemplate)">Create</button>
  </mat-card-actions>
</mat-card>
